import { MappingDatum, Point, RangePoint } from '../interface';
import Geometry, { GeometryCfg } from './base';
import Element from './element';
/** 引入对应的 ShapeFactory */
import './shape/line';
/** Path 构造函数参数类型 */
export interface PathCfg extends GeometryCfg {
    /** 是否连接空值 */
    connectNulls?: boolean;
}
/**
 * Path 几何标记。
 * 用于绘制路径图等。
 */
export default class Path extends Geometry {
    readonly type: string;
    readonly shapeType: string;
    /** 是否连接空值 */
    connectNulls: boolean;
    constructor(cfg: PathCfg);
    /**
     * 创建所有的 Element 实例，对于 Path、Line、Area，一组数据对应一个 Element。
     * @param mappingData
     * @param [isUpdate]
     * @returns elements
     */
    protected createElements(mappingData: MappingDatum[], isUpdate?: boolean): Element[];
    /**
     * 获取组成一条线（一组数据）的所有点数据。
     * @param mappingData
     * @returns points
     */
    protected getPoints(mappingData: MappingDatum[]): Point[] | RangePoint[];
    private getShapeInfo;
    private getData;
}
